import React, { useState } from 'react';
import { useLoaderData } from 'react-router-dom';
import { styled } from 'styled-components';
const VND = new Intl.NumberFormat('vi-VN', {
  style: 'currency',
  currency: 'VND',
});

const AdminProducts = () => {
  let productId = '';
  const productsData = useLoaderData();
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [category, setCategory] = useState('');
  const [oldprice, setOldprice] = useState('');
  const [newprice, setNewprice] = useState('');
  const [saleoff, setSaleoff] = useState('');
  const [image, setImage] = useState('');
  const [formIsShow, setFormIsShow] = useState(false);
  const [isEdited, setIsEdited] = useState(false);
  const [products, setProducts] = useState(productsData);

  return (
    <Container>
      {formIsShow && (
        <Backdrop>
          <AddForm onSubmit={submitHandler}>
            <InputBlock>
              <Label htmlFor="name">Tiêu đề</Label>
              <Input
                type="text"
                placeholder="Nhập tiêu đề"
                id="name"
                name="name"
                value={title}
                onChange={(e) => {
                  console.log(productId);
                  setTitle(e.target.value);
                }}
                autoComplete="off"
                required
              />
            </InputBlock>
            <InputBlock>
              <Label htmlFor="name">Tác giả</Label>
              <Input
                type="text"
                placeholder="Nhập tác giả"
                id="name"
                name="name"
                value={author}
                onChange={(e) => {
                  setAuthor(e.target.value);
                }}
                autoComplete="off"
                required
              />
            </InputBlock>
            <InputBlock>
              <Label htmlFor="category">Thể loại</Label>

              <select
                id="category"
                name="category"
                onChange={(e) => setCategory(e.target.value)}
              >
                <option value="manga">Manga</option>
                <option value="van-hoc">Văn học</option>
                <option value="thieu-nhi">Thiếu nhi</option>
              </select>
            </InputBlock>
            <InputBlock>
              <Label htmlFor="oldprice">Giá cũ</Label>
              <Input
                type="text"
                placeholder="Nhập tiêu đề"
                id="oldprice"
                name="oldprice"
                value={VND.format(oldprice)}
                onChange={(e) => {
                  setOldprice(e.target.value);
                }}
                autoComplete="off"
                required
              />
            </InputBlock>
            <InputBlock>
              <Label htmlFor="saleoff">Giảm giá</Label>
              <Input
                type="text"
                placeholder="Nhập tiêu đề"
                id="saleoff"
                name="saleoff"
                value={saleoff}
                onChange={(e) => {
                  setNewprice(e.target.value);
                }}
                autoComplete="off"
              />
            </InputBlock>
            <InputBlock>
              <Label htmlFor="newprice">Giá mới</Label>
              <Input
                type="text"
                placeholder="Nhập tiêu đề"
                id="newprice"
                name="newprice"
                value={VND.format(newprice)}
                onChange={(e) => {
                  setNewprice(e.target.value);
                }}
                autoComplete="off"
                required
                disabled
              />
            </InputBlock>
            <InputBlock>
              <Label htmlFor="image">Hình ảnh</Label>
              <input
                type="file"
                id="image"
                name="image"
                accept="image/png, image/jpeg"
                onChange={(event) => {
                  setImage(event.target.files[0]);
                }}
              />
              {image && (
                <img
                  id="frame"
                  src={URL.createObjectURL(image)}
                  width="100px"
                  height="100px"
                />
              )}
            </InputBlock>
            <FormActions>
              <CancelButton
                onClick={() => {
                  setFormIsShow(false);
                  setTitle('');
                  setAuthor('');
                  setNewprice('');
                  setOldprice('');
                  setSaleoff('');
                  setIsEdited(false);
                }}
                type="button"
              >
                Cancel
              </CancelButton>
              <SubmitButton>Submit</SubmitButton>
            </FormActions>
          </AddForm>
        </Backdrop>
      )}

      <NewProductBtn onClick={() => setFormIsShow(true)}>
        <img src="/images/plus.svg" alt="" />
        Thêm sản phẩm
      </NewProductBtn>
      <ProductsList>
        {products.map((product) => (
          <ProductItem key={product._id}>
            <img src={product.imageURL} alt="" />
            <ProductInfo>
              <ProductTitle>{product.title}</ProductTitle>
              <ProductAuthor>Tác giả: </ProductAuthor>
              <ProductPrice>Giá mới: {VND.format(product.price)}</ProductPrice>
              <ProductOldPrice>
                Giá cũ: {VND.format(product.oldprice)}
              </ProductOldPrice>
              <ProductSaleOff>Giảm giá: {product.saleoff}%</ProductSaleOff>
            </ProductInfo>
            <Actions>
              <button
                onClick={() => {
                  productId = product._id;
                  setTitle(product.title);
                  setAuthor(product.author);
                  setNewprice(product.price);
                  setOldprice(product.oldprice);
                  setSaleoff(product.saleoff);
                  setFormIsShow(true);
                  setIsEdited(true);
                }}
              >
                <img src="/images/edit.svg" alt="" />
              </button>
              <button>
                <img src="/images/admin-trash-can.svg" alt="" />
              </button>
            </Actions>
          </ProductItem>
        ))}
      </ProductsList>
    </Container>
  );
};

