import React from 'react';
import { styled } from 'styled-components';

const CheckOutPage = () => {
  return (
    <Container>
      <h1>GIỎ HÀNG</h1>
      <Inner>
        <LeftSide>
          <Header>
            <span>Số lượng</span>
            <span>Thành tiền</span>
          </Header>
          <Main>
            <ProductImage
              src="https://cdn0.fahasa.com/media/catalog/product//b/l/bluelock_bia_tap-12-1.jpg"
              alt=""
            />
            <ProductInfo>
              <ProductTitle>BlueLock - Tập 12 - Tặng Kèm Card PVC</ProductTitle>
              <div>
                <ProductNewPrice>29.750 đ</ProductNewPrice>
                <ProductOldPrice>35.000 đ</ProductOldPrice>
              </div>
            </ProductInfo>
            <ProductQty></ProductQty>
            <ProductTotalPrice></ProductTotalPrice>
          </Main>
        </LeftSide>
        <RightSide></RightSide>
      </Inner>
    </Container>
  );
};

export default CheckOutPage;

const Container = styled.div`
  max-width: 1230px;
  margin: 80px auto 0;
  h1 {
    margin: 0;
    font-weight: 600;
    color: #333333;
    line-height: 20px;
    font-size: 17px;
  }
`;

const Inner = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
`;

const LeftSide = styled.div`
  /* background-color: #fff; */
`;

const RightSide = styled.div``;

const Header = styled.div`
  padding-left: 30px;
  display: flex;
  justify-content: flex-end;
  background-color: white;
  margin-bottom: 10px;
  border-radius: 8px;
  padding: 10px;
  padding-right: 100px;
  span {
    margin-left: 50px;
  }
`;

const Main = styled.div`
  margin-top: 10px;
  background-color: #fff;
  padding: 20px 100px;
  border-radius: 8px;
  display: flex;
`;

const ProductImage = styled.img`
  width: auto;
  max-height: 119px;
  object-fit: contain;
`;

const ProductInfo = styled.div``;

const ProductQty = styled.div``;

const ProductTotalPrice = styled.span``;

const ProductOldPrice = styled.span``;

const ProductNewPrice = styled.span``;

const ProductTitle = styled.h2`
  font-size: 14px;
  color: #333333;

  word-break: break-word;
  white-space: normal;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  max-height: 6em;
  padding-top: 0px;
`;
