import React, { useState, useEffect } from 'react';
import { styled } from 'styled-components';
import LeftSide from '../components/Category';

const DUMMY_BOOKS = [
  { price: 1500000, category: 'thieu-nhi' },
  { price: 550000, category: 'manga' },
  { price: 350000, category: 'thieu-nhi' },
  { price: 250000, category: 'van-hoc' },
  { price: 550000, category: 'van-hoc' },
  { price: 50000, category: 'manga' },
];

const HomePage = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);

  useEffect(() => {
    setFilteredBooks(DUMMY_BOOKS);
  }, []);

  const PriceChangeHandler = (min, max) => {
    let updatedBooks = DUMMY_BOOKS.filter((book) => {
      const meetsPriceCriteria = max
        ? book.price >= min && book.price <= max
        : book.price >= min;
      const meetsCategoryCriteria =
        filteredBooks.length === 0 || filteredBooks.includes(book);
      return meetsPriceCriteria && meetsCategoryCriteria;
    });

    console.log('filteredByPrice', updatedBooks);
    setFilteredBooks(updatedBooks);
  };

  const CategoryChangeHandler = (category) => {
    let updatedBooks = DUMMY_BOOKS.filter((book) => {
      const meetsCategoryCriteria = book.category === category;
      const meetsPriceCriteria =
        filteredBooks.length === 0 || filteredBooks.includes(book);
      return meetsCategoryCriteria && meetsPriceCriteria;
    });

    console.log('filteredByCategory', updatedBooks);
    setFilteredBooks(updatedBooks);
  };

  useEffect(() => {
    setBooks(filteredBooks);
  }, [filteredBooks]);

  return (
    <Container>
      <LeftSide
        onPriceChange={PriceChangeHandler}
        onCategoryChange={CategoryChangeHandler}
      />
    </Container>
  );
};

const Container = styled.div`
  display: grid;
  grid-template-areas: 'leftside main';
  grid-template-columns: minmax(0, 2fr) minmax(0, 5fr);
  max-width: 1230px;
  height: 1000px;
  margin: 90px auto;
`;

export default HomePage;
