import React, { useState, useEffect } from 'react';
import { styled } from 'styled-components';
import LeftSide from '../components/Category';
const DUMMY_BOOKS = [
  { price: 1500000, category: 'thieu-nhi' },
  { price: 550000, category: 'manga' },
  { price: 350000, category: 'thieu-nhi' },
  { price: 250000, category: 'van-hoc' },
  { price: 550000, category: 'van-hoc' },
  { price: 50000, category: 'manga' },
];
let isFiltered = false;
const HomePage = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);

  useEffect(() => {
    setFilteredBooks(DUMMY_BOOKS);
  }, []);

  const PriceChangeHandler = (min, max) => {
    console.log(isFiltered);
    let updatedBooks;
    if (!isFiltered) {
      updatedBooks = filteredBooks.filter((book) =>
        max ? book.price >= min && book.price <= max : book.price >= min
      );
      isFiltered = true;
    } else {
      updatedBooks = books.filter((book) =>
        max ? book.price >= min && book.price <= max : book.price >= min
      );
    }
    console.log('books', books);
    console.log('filteredByPrice', updatedBooks);

    setFilteredBooks(updatedBooks);
  };

  const CategoryChangeHandler = (category) => {
    let updatedBooks;
    if (!isFiltered) {
      updatedBooks = filteredBooks.filter((book) => book.category === category);
      isFiltered = true;
    } else {
      console.log(isFiltered);
      updatedBooks = books.filter((book) => book.category === category);
    }
    console.log('books', books);

    console.log('filteredByCategory', updatedBooks);

    setFilteredBooks(updatedBooks);
  };

  useEffect(() => {
    setBooks(filteredBooks);
  }, [filteredBooks]);

  return (
    <Container>
      <LeftSide
        onPriceChange={PriceChangeHandler}
        onCategoryChange={CategoryChangeHandler}
      />
    </Container>
  );
};

const Container = styled.div`
  display: grid;
  grid-template-areas: 'leftside main';
  grid-template-columns: minmax(0, 2fr) minmax(0, 5fr);
  max-width: 1230px;
  height: 1000px;
  margin: 90px auto;
`;

export default HomePage;
