import React from 'react';
import { NavLink } from 'react-router-dom';
import { styled } from 'styled-components';
import { useNavigate } from 'react-router-dom';

const SideBar = () => {
  const navigate = useNavigate();
  return (
    <Container>
      <img src="/images/logo.svg" alt="" />
      <h1>ADMIN</h1>
      <NavLink
        to="/admin/products"
        className={({ isActive }) => (isActive ? 'active' : '')}
      >
        Sản phẩm
      </NavLink>
      <NavLink
        to="/admin/orders"
        className={({ isActive }) => (isActive ? 'active' : '')}
      >
        Đơn hàng
      </NavLink>
      <button
        onClick={() => {
          localStorage.removeItem('token');
          navigate('/');
        }}
      >
        Đăng xuất
      </button>
    </Container>
  );
};

export default SideBar;

const Container = styled.div`
  grid-area: sidebar;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #fff;
  height: 100vh;
  padding: 20px 0;
  box-shadow: 0 0 2px rgba(0, 0, 0, 0.1);
  h1 {
    color: #333333;
    margin: 0;
    margin-bottom: 50px;
    font-size: 25px;
  }
  img {
    width: 100px;
    height: 100px;
    margin-bottom: 50px;
  }
  a {
    display: block;
    width: 80%;
    height: 40px;
    font-size: 20px;
    font-weight: 700;
    text-align: center;
    border-radius: 10px;
    padding: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #333333;
    transition: all 200ms ease-in;
  }
  .active {
    background-color: #004aad;
    color: #fff;
  }
  button {
    cursor: pointer;
    width: 50%;
    height: 40px;
    border-radius: 10px;
    margin-top: auto;
    margin-bottom: 50px;
    background-color: #b22222;
    color: #fff;
    border: none;
    font-size: 20px;
    font-weight: bold;
  }
`;
