import React from 'react';
import { useSelector } from 'react-redux';
import { useLoaderData } from 'react-router-dom';
import { styled } from 'styled-components';

const OrdersPage = () => {
  const orderData = useLoaderData();
  const user = useSelector((state) => state.user.user);

  return (
    <Container>
      <LeftSide>
        <Account>
          <Avatar>
            <img src="./images/user-order.svg" alt="" />
          </Avatar>
          <Name>{user ? user.name : ''}</Name>
        </Account>
        <OrderIcon>
          <img src="./images/orders-icon.png" alt="" />
          <span>Đơn mua</span>
        </OrderIcon>
      </LeftSide>
      <RightSide>
        <h1>đơn hàng của tôi</h1>
        <OrdersList>
          {orderData.cart}
          <OrdersItem>
            <img src="" alt="" />
          </OrdersItem>
        </OrdersList>
      </RightSide>
    </Container>
  );
};

export default OrdersPage;

const Container = styled.div`
  max-width: 1230px;
  margin: 90px auto;
  height: 40px;
  display: grid;
  grid-template-columns: 1fr 6fr;
  column-gap: 30px;
`;

const LeftSide = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const Account = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  column-gap: 10px;
  padding: 10px 0;
  border-bottom: 0.0625rem solid rgba(0, 0, 0, 0.09);
`;

const Avatar = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border: 0.0625rem solid rgba(0, 0, 0, 0.09);
  width: 50px;
  height: 50px;
  border-radius: 50%;

  img {
    width: 24px;
    height: 24px;
  }
`;

const Name = styled.span`
  color: #333;
  font-weight: 600;
`;

const RightSide = styled.div`
  background-color: #fff;
`;

const OrderIcon = styled.div`
  margin-top: 30px;
  display: flex;
  align-items: center;
  font-size: 16px;
  font-weight: 500;
  color: #ee4d2d;
  column-gap: 20px;
  img {
    width: 20px;
    height: 20px;
  }
`;

const OrdersItem = styled.div``;
