import React from 'react';
import { styled } from 'styled-components';

const CartCheck = () => {
  return (
    <>
      <Container>
        <div>
          <Image
            src="https://cdn0.fahasa.com/media/catalog/product//c/o/combo-1707202300.jpg"
            alt=""
          />
          <Title>Món Quà Học Tập Tiện Lợi</Title>
        </div>
        <div>
          <Prices>
            <NewPrice>46,899đ</NewPrice>
            <OldPrice>93,500đ</OldPrice>
          </Prices>
          <Quantity>
            <span>Số lượng: </span>1
          </Quantity>
          <TotalPrice>46,899đ</TotalPrice>
        </div>
      </Container>
      <Mobile>
        <div>
          <Image
            src="https://cdn0.fahasa.com/media/catalog/product//c/o/combo-1707202300.jpg"
            alt=""
          />
        </div>
        <div>
          <Title>Món Quà Học Tập Tiện Lợi</Title>
          <Prices>
            <NewPrice>46,899đ</NewPrice>
            <OldPrice>93,500đ</OldPrice>
          </Prices>
          <Quantity>
            <span>Số lượng: </span>1
          </Quantity>
          <TotalPrice>46,899đ</TotalPrice>
        </div>
      </Mobile>
    </>
  );
};

export default CartCheck;

const Title = styled.h3`
  font-weight: normal;
  font-size: 15px;
`;
const Container = styled.div`
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  padding: 10px 0;
  border-bottom: 1px solid #ced4da;
  div {
    display: flex;
  }

  @media screen and (max-width: 991px) {
  }
`;

const Image = styled.img`
  max-width: 100%;
  max-height: 145px;
`;

const Prices = styled.div`
  width: 100px;
  display: flex;
  flex-direction: column;
  font-size: 15px;
  @media screen and (max-width: 991px) {
    display: flex;
    flex-direction: row;
  }
`;

const OldPrice = styled.span`
  color: #bfbfbf;
  text-decoration: line-through;
  font-size: 0.95em;
`;

const NewPrice = styled.span`
  @media screen and (max-width: 991px) {
    color: #f39801;
    font-weight: 900;
  }
`;

const Quantity = styled.span`
  width: 100px;
  text-align: center;
  font-size: 15px;
  span {
    display: none;
  }
  @media screen and (max-width: 991px) {
    span {
      display: inline-block;
    }
  }
`;

const TotalPrice = styled.span`
  width: 100px;
  color: #f39801;
  font-weight: 600;
  @media screen and (max-width: 991px) {
    display: none;
  }
`;
