import { checkAuthLoader } from '../../util/auth';
export async function loader() {
  const token = getAuthToken();
  if (!token) {
    return redirect('/login');
  }
  return fetch('http://localhost:8080/shop/order')
    .then((response) => {
      return response.json();
    })
    .then((resData) => {
      return resData.products;
    })
    .catch((err) => {
      console.log(err);
    });
}
