  //       return res.json();
  //     })
  //     .then((resData) => {})
  //     .catch((err) => {
  //       console.log(err);
  //     });
}

export function checkAuthLoader() {
  const token = getAuthToken();
  if (!token) {
    return redirect('/login');
  }
  return null;
}
