import React, { useState } from 'react';
import { useLoaderData } from 'react-router-dom';
import { styled } from 'styled-components';
const VND = new Intl.NumberFormat('vi-VN', {
  style: 'currency',
  currency: 'VND',
});

const AdminProducts = () => {
  const productsData = useLoaderData();
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [category, setCategory] = useState('');
  const [oldprice, setOldprice] = useState('');
  const [newprice, setNewprice] = useState('');
  const [saleoff, setSaleoff] = useState('');
  const [formIsShow, setFormIsShow] = useState(false);
  const [products, setProducts] = useState(productsData);
  return (
    <Container>
      <Backdrop>
        <AddForm>
          <InputBlock>
            <Label htmlFor="name">Tiêu đề</Label>
            <Input
              type="text"
              placeholder="Nhập tiêu đề"
              id="name"
              name="name"
              onChange={(e) => {
                // setName(e.target.value);
              }}
              autoComplete="off"
              required
            />
          </InputBlock>

          <InputBlock>
            <Label htmlFor="name">Tác giả</Label>
            <Input
              type="text"
              placeholder="Nhập tác giả"
              id="name"
              name="name"
              onChange={(e) => {
                // setName(e.target.value);
              }}
              autoComplete="off"
              required
            />
          </InputBlock>
          <InputBlock>
            <Label htmlFor="category">Thể loại</Label>
            <select id="category" name="category">
              <option value="manga">Manga</option>
              <option value="van-hoc">Văn học</option>
              <option value="thieu-nhi">Thiếu nhi</option>
            </select>
          </InputBlock>
          <InputBlock>
            <Label htmlFor="oldprice">Giá cũ</Label>
            <Input
              type="text"
              placeholder="Nhập tiêu đề"
              id="oldprice"
              name="oldprice"
              onChange={(e) => {
                // setName(e.target.value);
              }}
              autoComplete="off"
              required
            />
          </InputBlock>
          <InputBlock>
            <Label htmlFor="saleoff">Giảm giá</Label>
            <Input
              type="text"
              placeholder="Nhập tiêu đề"
              id="saleoff"
              name="saleoff"
              onChange={(e) => {
                // setName(e.target.value);
              }}
              autoComplete="off"
            />
          </InputBlock>
          <InputBlock>
            <Label htmlFor="newprice">Giá mới</Label>
            <Input
              type="text"
              placeholder="Nhập tiêu đề"
              id="newprice"
              name="newprice"
              onChange={(e) => {
                // setName(e.target.value);
              }}
              autoComplete="off"
              required
              disabled
            />
          </InputBlock>
          <InputBlock>
            <Label htmlFor="name">Giá mới</Label>
            <input
              type="file"
              id="avatar"
              name="avatar"
              accept="image/png, image/jpeg"
            />
          </InputBlock>
        </AddForm>
      </Backdrop>
      <NewProductBtn>
        <img src="/images/plus.svg" alt="" />
        Thêm sản phẩm
      </NewProductBtn>
      <ProductsList>
        {products.map((product) => (
          <ProductItem>
            <img src={product.imageURL} alt="" />
            <ProductInfo>
              <ProductTitle>{product.title}</ProductTitle>
              <ProductAuthor>Tác giả: </ProductAuthor>
              <ProductPrice>Giá mới: {VND.format(product.price)}</ProductPrice>
              <ProductOldPrice>
                Giá cũ: {VND.format(product.oldprice)}
              </ProductOldPrice>
              <ProductSaleOff>Giảm giá: {product.saleoff}%</ProductSaleOff>
            </ProductInfo>
            <Actions>
              <button>
                <img src="/images/edit.svg" alt="" />
              </button>
              <button>
                <img src="/images/admin-trash-can.svg" alt="" />
              </button>
            </Actions>
          </ProductItem>
        ))}
      </ProductsList>
    </Container>
  );
};

export default AdminProducts;

const Container = styled.div`
  grid-area: main;
  padding: 100px 20px;
  width: 80%;
  margin: 0 auto;
`;

const ProductsList = styled.ul`
  list-style: none;
  width: 100%;
  height: 500px;
  border: 5px solid rgba(0, 0, 0, 0.09);
  overflow: scroll;
  padding: 20px;
`;

const ProductItem = styled.li`
  margin-bottom: 10px;
  border-radius: 20px;
  height: 150px;
  display: grid;
  padding: 20px;
  grid-template-columns: 1fr 6fr 1fr;
  /* align-items: center; */
  background-color: #fff;
  img {
    width: 80px;
  }
`;

const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  font-size: 14px;
`;

const ProductTitle = styled.h2`
  font-size: 16px;
  font-weight: 500;
  margin: 0;
`;

const ProductPrice = styled.span``;

const Actions = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  button {
    border: none;
    background: transparent;
    width: 40px;
    height: 40px;
    img {
      width: 100%;
      height: 100%;
    }
  }
  button:first-child {
    width: 35px;
    height: 35px;
  }
`;

const ProductAuthor = styled.span``;

const ProductOldPrice = styled.span``;

const ProductSaleOff = styled.span``;

const NewProductBtn = styled.button`
  display: block;
  width: 150px;
  height: 40px;
  border-radius: 999px;
  font-weight: bold;
  color: #fff;
  background-color: #004aad;
  border: none;
  margin-left: auto;
  display: flex;
  align-items: center;
  justify-content: center;
  column-gap: 10px;
  img {
    width: 20px;
    height: 20px;
  }
`;

const AddForm = styled.form`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #fff;
  padding: 30px;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const Input = styled.input`
  width: 500px;
  height: 30px;
  padding: 4px 17px;
  border: 1px solid #ced4da;
  transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
  border-radius: 0.25rem;
`;
const Label = styled.label`
  font-size: 14px;
  color: #333333;
  width: 80px;
  padding: 4px;
  margin-bottom: 5px;
  text-align: left;
`;
const InputBlock = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  width: 100%;
  padding: 8px 0;
  margin-bottom: 20px;
  position: relative;
  @media screen and (max-width: 767px) {
    flex-direction: column;
    align-items: flex-start;
  }
`;

const Backdrop = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 9999;
`;
