import React from 'react';
import { styled } from 'styled-components';
const CheckOut = () => {
  return (
    <Container>
      <img src="/images/cart.svg" alt="" />
      <span>Giỏ hàng</span>
      <Badge>1</Badge>
    </Container>
  );
};

export default CheckOut;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const Badge = styled.div`
  width: 18px;
  height: 18px;
  position: absolute;
  font-size: 13px;
  top: 0;
  right: 0;
  border-radius: 50%;
  background-color: red;
`;
