import React, { createContext, useState } from 'react';
import { styled } from 'styled-components';
import LeftSide from '../components/Category';
const DUMMY_BOOKS = [
  { price: 1500000 },
  { price: 550000 },
  { price: 350000 },
  { price: 250000 },
  { price: 150000 },
  { price: 50000 },
];
const HomePage = () => {
  const [books, setBooks] = useState(DUMMY_BOOKS);
  const PriceChangeHandler = (min, max) => {
    const filteredByPrice = books.filter(
      (book) => book.price >= min && book.price <= max
    );
  };
  return (
    <Container>
      <LeftSide onPriceChange={PriceChangeHandler} />
    </Container>
  );
};

const Container = styled.div`
  display: grid;
  grid-template-areas: 'leftside main';
  grid-template-columns: minmax(0, 2fr) minmax(0, 5fr);
  max-width: 1230px;
  height: 1000px;
  margin: 90px auto;
`;

export default HomePage;
