import React from 'react';
import { styled } from 'styled-components';
import { Link } from 'react-router-dom';
import * as ROUTES from '../constants/routes';
const Header = () => {
  return (
    <Container>
      <Inner>
        <Logo src="/images/logo.svg" alt="" />
        <Search>
          <input type="text" placeholder="Tìm kiếm sản phẩm mong muốn..." />
          <button>
            <img src="/images/search-logo.svg" alt="" />
          </button>
        </Search>
        <Nav>
          <Link to={ROUTES.CHECKOUT}>
            <Checkout>
              <img src="/images/cart.svg" alt="" />
              <span>Giỏ hàng</span>
            </Checkout>
          </Link>
          <User>
            <img src="/images/user.svg" alt="" />
            <span>Tài khoản</span>
            <Dropdown>
              <Link to={ROUTES.LOGIN}>
                <button>Đăng nhập</button>
              </Link>
              <Link to={ROUTES.SIGNUP}>
                <button>Đăng ký</button>
              </Link>
            </Dropdown>
          </User>
        </Nav>
      </Inner>
    </Container>
  );
};

const Container = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;

  background-color: #fff;
`;

const Inner = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 1230px;
  height: 60px;
  margin: 0 auto;
`;

const Logo = styled.img`
  height: 100%;
`;

const Search = styled.div`
  width: 600px;
  height: 38px;
  border: 1px solid black;
  display: flex;
  align-items: center;
  padding-left: 24px;
  padding-right: 3px;
  border-radius: 5px;
  input {
    width: 100%;
    border: none;
    outline: none;
  }
  button {
    width: 60px;
    height: 80%;
    background-color: #2ee5ab;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    img {
      width: 18px;
      height: 18px;
    }
  }
`;

const Nav = styled.nav`
  line-height: 18px;
  font-size: 13px;
  width: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  column-gap: 20px;
  img {
    width: 18px;
    height: 18px;
    color: #7a7e7f;
  }

  a {
    color: black;
  }
`;
const User = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const Dropdown = styled.div`
  position: absolute;
  width: 300px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;

  button: ;
`;

const Checkout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export default Header;
