import React, { useState } from 'react';
import { useLoaderData } from 'react-router-dom';
import { styled } from 'styled-components';

const AdminProducts = () => {
  const productsData = useLoaderData();
  console.log(productsData);
  const [products, setProducts] = useState(productsData);
  return (
    <Container>
      <ProductsList>
        {products.map((product) => (
          <ProductItem>
            <img src={product.imageURL} alt="" />
            <ProductInfo>
              <ProductTitle>{product.title}</ProductTitle>
              <ProductAuthor>Tác giả: </ProductAuthor>
              <ProductPrice>Giá mới: {product.price}</ProductPrice>
              <ProductOldPrice>Giá cũ: {product.oldprice}</ProductOldPrice>
              <ProductSaleOff>{product.SaleOff}</ProductSaleOff>
            </ProductInfo>
            <Actions>
              <button>Edit</button>
              <button>Delete</button>
            </Actions>
          </ProductItem>
        ))}
      </ProductsList>
    </Container>
  );
};

export default AdminProducts;

const Container = styled.div`
  grid-area: main;
  padding: 100px 20px;
  width: 80%;
  margin: 0 auto;
`;

const ProductsList = styled.ul`
  list-style: none;
  width: 100%;
`;

const ProductItem = styled.li`
  margin-bottom: 20px;
  height: 150px;
  display: grid;
  padding: 20px;
  grid-template-columns: 1fr 6fr 1fr;
  /* align-items: center; */
  background-color: #fff;
  img {
    width: 80px;
  }
`;

const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const ProductTitle = styled.h2`
  font-size: 18px;
  font-weight: 500;
  margin: 0;
`;

const ProductPrice = styled.span``;

const Actions = styled.div`
  display: flex;
  flex-direction: column;
`;

const ProductAuthor = styled.span``;

const ProductOldPrice = styled.span``;

const ProductSaleOff = styled.span``;
